package io.github.aj8gh.leetcode.string;

import static org.junit.jupiter.api.Assertions.assertEquals;

import java.util.stream.Stream;
import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

class MinWindowTest {

  private static Stream<Arguments> inputProvider() {
    return Stream.of(
        Arguments.of("ADOBECODEBANC", "ABC", "BANC"),
        Arguments.of("ab", "a", "a"),
        Arguments.of(
            "",
            "ozgzyywxvtublcl",
            "tcnvhxqgndyozpcigzykbiaucyvwrjvknifufxducbkbsmlanl")
    );
  }

  @ParameterizedTest
  @MethodSource("inputProvider")
  void minWindow(String s, String t, String expectedOutput) {
    var minWindow = new MinWindow();
    var actualOutput = minWindow.minWindow(s, t);
    assertEquals(expectedOutput, actualOutput);
  }
}
